INSERT INTO `wp_mp_products` (`id`, `site_id`, `blog_id`, `blog_public`, `post_id`, `post_author`, `post_title`, `post_content`, `post_permalink`, `post_date`, `post_date_gmt`, `post_modified`, `post_modified_gmt`, `price`, `sales_count`) VALUES
(1, 1, 6, 0, 8, 5, 'Classic Bread', '<span style="color: #4f3f2e">A handmade, classic, wholesome loaf made using organic stone-ground flour that’s been traditionally milled in Wales or the Cotswolds and without any artificial ingredients or preservatives. This is what bread used to taste like.</span>', 'http://cardiganbasket.co.uk/localbakery/store/products/classic-bread/', '2014-04-27 16:22:03', '2014-04-27 16:22:03', '2014-04-27 16:22:03', '2014-04-27 16:22:03', '0.00', 0),
(2, 1, 6, 0, 12, 5, 'Sourdough Bread', '<span style="color: #4f3f2e">bA eautiful, long fermented, handmade sourdough made with organic flour milled in Wales or the Cotswolds, and using wild yeast cultures. Sourdough is all about the flavour, the crust and a crumb that’s light and airy – hence the famous sourdough holes – because of slow proving.</span><br style="color: #4f3f2e" /><br style="color: #4f3f2e" /><span style="color: #4f3f2e">Typical loaves on this subscription: Seeded Granary Sourdough; Caraway Rye; Pain au Levain (three stage sourdough with rye, wheat and whole white flour); Wholegrain Sourdough; Cherry, Fennel &amp; Rye; San Francisco Sourdough (the iconic sourdough; takes four days); Spiced Cheese &amp; Herb; Potato Sourdough; Raisin, Hazelnut &amp; Shallot; 100% Rye; Five-Grain Sourdough.</span>', 'http://cardiganbasket.co.uk/localbakery/store/products/sourdough-bread/', '2014-04-27 16:23:15', '2014-04-27 16:23:15', '2014-04-27 16:23:15', '2014-04-27 16:23:15', '0.00', 0),
(3, 1, 6, 0, 15, 5, 'Carrot Cupcake', '<p style="color: #000000">With hints of cinnamon and nutmeg, our carrot cupcake has a mosit spiced carrot sponge with a delicious orange cream cheese buttercream, decorated with a sugar carrot. This flavour is popular with all ages. Please note our carrot cupcakes do not contain nuts, but they do contain raisins. This flavour is very popular all year round, and is available in our bakeries every day, or to order online now.</p>\r\n&nbsp;\r\n<div id="AddToBasket" style="color: #000000"></div>', 'http://cardiganbasket.co.uk/localbakery/store/products/carrot-cupcake/', '2014-04-27 16:26:20', '2014-04-27 16:26:20', '2014-04-27 16:26:20', '2014-04-27 16:26:20', '0.00', 0),
(4, 1, 6, 0, 18, 5, 'Chocolate Cupcake', '<p style="color: #000000">This rich and moist dark chocolate sponge with a silky chocolate buttercream is one of our best sellers. They come decorated with a selection of sprinkles or flowers. This flavour is very popular all year round, and is available in our bakeries every day, or to order online now.</p>\r\n&nbsp;\r\n<div id="AddToBasket" style="color: #000000"></div>', 'http://cardiganbasket.co.uk/localbakery/store/products/chocolate-cupcake/', '2014-04-27 16:27:23', '2014-04-27 16:27:23', '2014-04-27 16:27:23', '2014-04-27 16:27:23', '0.00', 0),
(5, 1, 6, 0, 21, 5, 'Plain Croissant', '<p class="ecxMsoNormal" style="color: #000000">Baked fresh every day, these are one of our most popular items and often sell out at the weekends. Our croissants are served daily at our Primrose Hill bakery or to order online now.</p>\r\n&nbsp;\r\n<div id="AddToBasket" style="color: #000000"></div>', 'http://cardiganbasket.co.uk/localbakery/store/products/plain-croissant/', '2014-04-27 16:28:20', '2014-04-27 16:28:20', '2014-04-27 16:28:20', '2014-04-27 16:28:20', '0.00', 0),
(6, 1, 7, 0, 4, 6, 'cake', 'this is a test', 'http://cardiganbasket.co.uk/mark2/store/products/cake/', '2014-04-27 17:42:22', '2014-04-27 17:42:22', '2014-04-27 17:42:22', '2014-04-27 17:42:22', '0.00', 0),
(7, 1, 7, 0, 7, 6, 'Bread', 'this is a test2', 'http://cardiganbasket.co.uk/mark2/store/products/bread/', '2014-04-27 17:43:02', '2014-04-27 17:43:02', '2014-04-27 17:43:02', '2014-04-27 17:43:02', '0.00', 0),
(8, 1, 8, 0, 9, 7, 'Classic Bread', '<p class="p1"><span class="s1">Classic white sliced bread. Now even softer and better tasting. The ideal basis for hearty toasts, tasty sandwiches, or party snacks.</span></p>', 'http://cardiganbasket.co.uk/goodbakers/store/products/classic-bread/', '2014-04-28 01:24:10', '2014-04-28 01:24:10', '2014-04-28 01:24:10', '2014-04-28 01:24:10', '0.00', 0),
(9, 1, 8, 0, 12, 7, 'Sourdough Bread', 'A traditional g<span style="color: #666666">rainy and seedy sourdough loaf of bread.</span>', 'http://cardiganbasket.co.uk/goodbakers/store/products/sourdough-bread/', '2014-04-28 01:40:09', '2014-04-28 01:40:09', '2014-04-28 02:23:39', '2014-04-28 02:23:39', '2.20', 0),
(10, 1, 8, 0, 15, 7, 'Carrot Cupcake', 'Classic cinnamon and orange carrot cake with a delicious cream cheese &amp; orange infused frosting topped with chopped walnuts and sultanas', 'http://cardiganbasket.co.uk/goodbakers/store/products/carrot-cake/', '2014-04-28 01:40:59', '2014-04-28 01:40:59', '2014-04-28 02:18:53', '2014-04-28 02:18:53', '0.49', 0),
(11, 1, 8, 0, 18, 7, 'Chocolate Cupcake', 'Very indulgent double Belgian chocolate cupcake with a rich Belgian creamy chocolate ganache.', 'http://cardiganbasket.co.uk/goodbakers/store/products/chocolate-cupcake/', '2014-04-28 01:41:50', '2014-04-28 01:41:50', '2014-04-28 02:19:22', '2014-04-28 02:19:22', '0.49', 0),
(12, 1, 8, 0, 22, 7, 'Plain Croissant', '<span style="font-weight: bold;color: #545454">Plain Croissant</span><span style="color: #545454"> - A traditional buttery croissant, no frills, just delicious flaky goodness. </span>', 'http://cardiganbasket.co.uk/goodbakers/store/products/plain-croissant/', '2014-04-28 01:43:21', '2014-04-28 01:43:21', '2014-04-28 02:21:57', '2014-04-28 02:21:57', '1.49', 0);
